<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Products extends CI_Controller {	public $data = array();	public function __construct()	{		parent::__construct();                               date_default_timezone_set('UTC');                $this->load->model(array('backend/Model_menu_frontend','web/Model_label','web/Model_content'));		$this->load->helper(array('funcglobal','menu','accessprivilege','alias','text'));                $this->data['controller'] = $this->uri->segment(1);                $module_name=$this->data['controller'];                $this->data['menu_id']=  2;                               $getmodule = $this->Model_content->getModule($module_name);                foreach ($getmodule as $gm) {                 $this->module_id = $gm->module_id;                 $this->section = $gm->module_group_id;                 $_SESSION['module_id']=$this->module_id;                }				$this->data['controller'] = $module_name;                 $this->data['module_id']=$_SESSION['module_id'];                	}		public function index($index=null)                          	{                          if ($index !=''){            $row_id=$index;             }          else {            $row_id=112;            }          $this->detail($row_id);	}       function detail($row_id=NULL)	{                                       $whereContent = '';                $order_limit= '';                $whereContent .= "WHERE a.row_parent=0 and a.row_id = ".$row_id;                $order_limit .= " order by a.row_order ASC";                $ListContent = $this->Model_content->getListContent($whereContent,$order_limit,'');                $this->data["countContent"] = count($ListContent);//                echo '<!--<pre>';//                print_r($ListContent);//                echo '-->';		$this->data["ListContent"] = $ListContent;                               $this->load->view('vproducts' ,$this->data);			}    }